mapper.select:
select
n.id as nid, n.name as nname, p.id as pid, p.name as pname, d.id as did, d.name as dname, j.id as jid, j.name as jname, pos.id as posid, pos.name as posname from employee e, nation n, politics_status p, department d, joblevel j, position pos, user u where e.nationId = n.id and e.politicId = p.id and e.jobLevelId = j.id and e.departmentId = d.id and e.posId = pos.id and e.userId = u.id
AND e.name like concat('%',#{employee.name},'%')
AND e.politicId = #{employee.politicId}
AND e.nationId = #{employee.nationId}
AND e.jobLevelId = #{employee.jobLevelId}
AND e.posId = #{employee.posId}
AND e.departmentId = #{employee.departmentId}
AND e.engageForm = #{employee.engageForm}
AND e.beginDate between #{beginDateScope[0]} and #{beginDateScope[1]}
ORDER BY e.id
